import base64
import numpy as np
from io import BytesIO
from PIL import Image
import face_recognition
from pydantic import BaseModel
from models import Student

class ImageRequest(BaseModel):
    image_base64:str

class AiRecognition():
    def __init__(self):
        pass

    def base64_to_rgb(self,base64_string: ImageRequest):
        # Decodifica el string base64 a bytes
        image_data = base64.b64decode(base64_string)

        # Convierte los bytes a una imagen usando PIL
        image = Image.open(BytesIO(image_data))

        # Convierte la imagen a RGB si no está en ese formato
        image = image.convert('RGB')

        # Convierte la imagen a un array de numpy
        np_image = np.array(image)

        return np_image

    def recognize_faces(self,base64_string):
        # Convierte el string base64 a una imagen RGB
        rgb_image = self.base64_to_rgb(base64_string)
        
        # Usa face_recognition para detectar rostros en la imagen
        face_locations = face_recognition.face_locations(rgb_image)
        
        # Obtén los codificaciones faciales de la imagen
        face_encodings = face_recognition.face_encodings(rgb_image, face_locations)
        
        return face_locations, face_encodings

    def compare_faces(self, known_students, student_curr_encoding):
        # Almacena en una variable la lista de similitud con todas las codificaciones de rostros almacenados. 
        faceDis = face_recognition.face_distance(known_students,student_curr_encoding)
        # Obtener el indice de la codificacion que mas se aproxima al rostro mostrado en la camara web.
        matchIndex = np.argmin(faceDis)
        # retorna el estudiante encontrado
        return known_students[matchIndex]
# Ejemplo de uso
base64_string = ""
ia = AiRecognition()
face_locations, face_encodings = ia.recognize_faces(base64_string)

print("Ubicaciones de los rostros:", face_locations)
print("Codificaciones faciales:", face_encodings)

